void chime(string mess) {
  int *co_ords;

  if(environment(this_object())) {

    co_ords = (int *)environment(this_object())->query_co_ord();
    
    "/obj/handlers/broadcaster"->broadcast_event(users(), co_ords,
                                                 mess, 20, 1, 0 );
    tell_room(environment(this_object()), mess);
  }
}

