      sort_array(BOARD_HAND->list_killfile(req->user), 1);
    //current_order = BOARD_HAND->query_board_order(req->user);
    //boards = current_order + sort_array(boards - current_order, 1);
    
    tmp = ({ });
    tmp += get_next_new_note(req->user, newsrc, req, 0);
